mapper.select:
select * from hse_house


        and name like concat('%',#{name},'%')


        and community_id=#{communityId}


        and house_type_id=#{houseTypeId}


        and floor_id=#{floorId}


        and build_structure_id=#{buildStructureId}


        and direction_id=#{directionId}


        and decoration_id=#{decorationId}


        and house_use_id=#{houseUseId}

    and is_deleted=0

mapper.update:
update hse_house
set is_deleted=1
where id = #{id}

mapper.select:
-- hse_house hse_community select comm.`name` communityName, house.id, house.name, house.build_area buildArea, house.total_price totalPrice, house.unit_price unitPrice, house.house_type_id houseTypeId, house.floor_id floorId, house.direction_id directionId, house.default_image_url defaultImageUrl, house.create_time createTime from hse_house house LEFT JOIN hse_community comm on(house.community_id=comm.id)
and house.name like CONCAT('%',#{vo.keyword},'%')
and comm.area_id = #{vo.areaId}
and comm.plate_id = #{vo.plateId}
and house.house_type_id = #{vo.houseTypeId}
and house.floor_id = #{vo.floorId}
and house.build_structure_id = #{vo.buildStructureId}
and house.direction_id = #{vo.directionId}
and house.decoration_id = #{vo.decorationId}
and house.house_use_id = #{vo.houseUseId}
and house.is_deleted=0 and comm.is_deleted=0 and house.`status`=1
order by house.id desc
order by house.total_price desc
order by house.create_time desc
